export const Icon = ({
  name,
  className,
  strokeWidth,
  stroke,
  fill,
  size,
  ...props
}: IIconProps) => {
  const IconComponent = icons[name];

  if (!IconComponent) {
    console.warn(`Icon ${name} not found`);
    return null;
  }

  return (
    <IconComponent
      width={size}
      height={size}
      className={className}
      strokeWidth={strokeWidth}
      stroke={stroke}
      fill={fill}
      viewBox={`0 0 ${size} ${size}`}
      {...props}
    />
  );
};
